/* global Promise */

import React from 'react';
import { render } from 'react-dom';
import {
  NotificationsManager,
  ShowAction,
  CloseAction,
  CloseAllAction,
  SetPlacementAction,
  SetDurationAction,
  NotificationProps,
  Placement,
} from './NotificationsManager';
import type { NotificationVariant, NotificationCta } from './types';

export interface NotificationsAPI {
  success: ShowAction<Notification>;
  error: ShowAction<Notification>;
  show: ShowAction<Notification>;
  close: CloseAction<void>;
  closeAll: CloseAllAction<void>;
  setPlacement: SetPlacementAction<void>;
  setDuration: SetDurationAction<void>;
}

let initiated = false;
const internalAPI: Partial<NotificationsAPI> = {};

function registerAPI(fnName: string, fn: Function) {
  internalAPI[fnName] = fn;
}

function createRoot(callback: () => void) {
  const container = document.createElement('div');
  document.body.appendChild(container);

  render(<NotificationsManager register={registerAPI} />, container, callback);
}

function afterInit<PromiseValueType>(fn: Function) {
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  return (...args: any[]) => {
    if (!initiated) {
      initiated = true;
      return new Promise<PromiseValueType>((resolve) => {
        createRoot(() => {
          resolve(fn(...args));
        });
      });
    } else {
      return Promise.resolve<PromiseValueType>(fn(...args));
    }
  };
}

const show =
  (variant: NotificationVariant) =>
  (
    text: string,
    settings?: {
      duration?: number;
      withClose?: boolean;
      id?: string;
      title?: string;
      cta?: Partial<NotificationCta>;
    },
  ) => {
    if (internalAPI.show) {
      return internalAPI.show(text, {
        ...(settings || {}),
        variant,
      });
    }
  };

type ExternalShowAction<T> = (
  text: string,
  settings?: {
    duration?: number;
    withClose?: boolean;
    id?: string;
    title?: string;
    cta?: Partial<NotificationCta>;
  },
) => T;

export const Notification: {
  success: ExternalShowAction<Promise<NotificationProps>>;
  error: ExternalShowAction<Promise<NotificationProps>>;
  warning: ExternalShowAction<Promise<NotificationProps>>;
  info: ExternalShowAction<Promise<NotificationProps>>;
  close: CloseAction<Promise<void>>;
  closeAll: CloseAllAction<Promise<void>>;
  setPlacement: SetPlacementAction<Promise<void>>;
  setDuration: SetDurationAction<Promise<void>>;
} = {
  success: afterInit<NotificationProps>(show('positive')),
  error: afterInit<NotificationProps>(show('negative')),
  warning: afterInit<NotificationProps>(show('warning')),
  info: afterInit<NotificationProps>(show('primary')),
  close: afterInit<void>((id: string | number) => {
    if (internalAPI.close) {
      return internalAPI.close(id);
    }
  }),
  closeAll: afterInit<void>(() => {
    if (internalAPI.closeAll) {
      return internalAPI.closeAll();
    }
  }),
  setPlacement: afterInit<void>(
    (placement: Placement, params?: { offset: number }) => {
      if (internalAPI.setPlacement) {
        return internalAPI.setPlacement(placement, params);
      }
    },
  ),
  setDuration: afterInit<void>((duration: number) => {
    if (internalAPI.setDuration) {
      return internalAPI.setDuration(duration);
    }
  }),
};
import React, { useCallback, useState } from 'react';
import { cx } from 'emotion';

import { useAsyncState } from './useAsyncState';
import type { NotificationCta, NotificationVariant } from '../types';
import { NotificationItemContainer } from '../NotificationItem';
import { getStyles } from './NotificationsManager.styles';

export type Placement = 'top' | 'bottom';

export interface NotificationProps {
  id: string | number;
  text: string;
  onClose: Function;
  duration?: number;
  withClose: boolean;
  isShown: boolean;
  variant: NotificationVariant;
  title?: string;
  cta?: Partial<NotificationCta>;
}

export type ShowAction<T> = (
  text: string,
  setting?: {
    variant: NotificationVariant;
    id?: string;
    duration?: number;
    withClose?: boolean;
    title?: string;
    cta?: Partial<NotificationCta>;
  },
) => T;

export type CloseAction<T> = (id: string | number) => T;

export type CloseAllAction<T> = () => T;

export type SetDurationAction<T> = (duration: number) => T;

export type SetPlacementAction<T> = (
  placement: Placement,
  params?: { offset: number },
) => T;

export interface NotificationsManagerProps {
  register: (name: string, callback: Function) => void;
}

let uniqueId = 0;

const getUniqueId = (): number => {
  uniqueId += 1;
  return uniqueId;
};

export const NotificationsManager = ({
  register,
}: NotificationsManagerProps): React.ReactElement => {
  const [items, setItems] = useAsyncState<NotificationProps[]>([]);
  const [placement, setPlacementState] = useState('bottom');
  const [placementOffset, setPlacementOffset] = useState(20);
  const [duration, setDuration] = useState(6000);
  const styles = getStyles(placement, placementOffset);

  const setPlacement: SetPlacementAction<void> = useCallback(
    (placement, params?: { offset: number }) => {
      if (placement === 'bottom' || placement === 'top') {
        const placementOffset = params && params.offset ? params.offset : 20;
        setPlacementState(placement);
        setPlacementOffset(placementOffset);
      }
    },
    [],
  );

  const close: CloseAction<void> = useCallback(
    (id) => {
      setItems(
        items.current.map((item) => {
          if (item.id !== id) {
            return item;
          }

          return {
            ...item,
            isShown: false,
          };
        }),
      );
    },
    [items, setItems],
  );

  const closeAndDelete = useCallback(
    (id: string | number) => {
      setItems(items.current.filter((item) => item.id !== id));
    },
    [items, setItems],
  );

  const closeAll: CloseAllAction<void> = useCallback(() => {
    setItems(
      items.current.map((item) => {
        return {
          ...item,
          isShown: false,
        };
      }),
    );
  }, [items, setItems]);

  const show: ShowAction<NotificationProps> = useCallback(
    (text, settings) => {
      const itemDuration =
        settings && typeof settings.duration !== 'undefined' // Needed as 0 is falsy but 0 is valid to disable auto-closing a notification
          ? settings.duration
          : duration;
      const variant = settings?.variant ? settings.variant : 'positive';

      const withClose =
        settings && typeof settings.withClose !== 'undefined'
          ? settings.withClose
          : true;

      const notificationId = settings?.id ? settings.id : getUniqueId();

      const notification = {
        id: notificationId,
        text,
        onClose: () => closeAndDelete(notificationId),
        duration: itemDuration,
        withClose,
        isShown: true,
        variant,
        title: settings?.title,
        cta: settings?.cta,
      };

      const alreadyThere = items.current.find(
        (item) => item.id === notification.id,
      );

      if (alreadyThere) {
        return alreadyThere;
      }

      if (placement === 'top') {
        setItems([notification, ...items.current]);
      } else {
        setItems([...items.current, notification]);
      }

      return notification;
    },
    [closeAndDelete, duration, items, placement, setItems],
  );

  register('close', close);
  register('show', show);
  register('closeAll', closeAll);
  register('setPlacement', setPlacement);
  register('setDuration', setDuration);

  return (
    <div
      data-test-id="cf-notification-container"
      className={cx(styles.manager)}
    >
      <div className={cx(styles.container)}>
        {items.current.map((item) => (
          <NotificationItemContainer
            variant={item.variant}
            duration={item.duration}
            key={item.id}
            withCloseButton={item.withClose}
            // eslint-disable-next-line react/jsx-handler-names
            onClose={item.onClose}
            isShown={item.isShown}
            title={item.title}
            cta={item.cta}
          >
            {item.text}
          </NotificationItemContainer>
        ))}
      </div>
    </div>
  );
};

NotificationsManager.displayName = 'NotificationsManager';
import { useRef, useState } from 'react';

/**
 * An asyncronous state hook.
 *
 * Think of it as a slight modification of React's `useState` hook, where the
 * state returned is instead a `MutableRefObject` like you know it from the
 * `useRef` hook.
 *
 * @param value
 * @returns A tuple with the `MutableRefObject` and a function to set the state
 */
export function useAsyncState<ValueType>(
  value: ValueType,
): [React.MutableRefObject<ValueType>, (state: ValueType) => void] {
  const ref = useRef<ValueType>(value);
  const [, forceUpdate] = useState(false);

  const setState = (newState: ValueType) => {
    if (!Object.is(ref.current, newState)) {
      ref.current = newState;
      forceUpdate((state) => !state);
    }
  };

  return [ref, setState];
}
import React from 'react';
import { cx } from 'emotion';
import {
  CheckCircleIcon,
  ErrorCircleIcon,
  WarningIcon,
  CloseIcon,
  InfoCircleIcon,
} from '@contentful/f36-icons';
import { Button } from '@contentful/f36-button';
import { TextLink } from '@contentful/f36-text-link';
import {
  Flex,
  Box,
  type ExpandProps,
  type CommonProps,
} from '@contentful/f36-core';
import { Heading, Paragraph } from '@contentful/f36-typography';

import type { NotificationVariant, NotificationCta } from '../types';
import { getStyles } from './NotificationItem.styles';

export interface NotificationItemProps extends CommonProps {
  /**
   * Defines the styling of notification
   * @default positive
   */
  variant?: NotificationVariant;
  /**
   * Defines if the close button should be rendered
   * @default true
   */
  withCloseButton?: boolean;
  /**
   * Function that will be triggered when close button is clicked
   */
  onClose?: Function;
  /**
   * Title of the notification
   */
  title?: string;
  /**
   * Content of the notificaiton
   */
  children: React.ReactNode;
  /**
   * Label and text-link props of the CTA
   */
  cta?: Partial<NotificationCta>;
}

const _NotificationItem = (props: ExpandProps<NotificationItemProps>, ref) => {
  const {
    className,
    children,
    cta,
    withCloseButton = true,
    variant = 'positive',
    onClose,
    testId = 'cf-ui-notification',
    title,
    ...otherProps
  } = props;

  const styles = getStyles({ variant });

  const iconSize = title ? 'medium' : 'small';
  const iconVariants = {
    positive: <CheckCircleIcon variant={variant} size={iconSize} />,
    warning: <WarningIcon variant={variant} size={iconSize} />,
    negative: <ErrorCircleIcon variant={variant} size={iconSize} />,
    primary: <InfoCircleIcon variant={variant} size={iconSize} />,
  };

  const intents = {
    positive: 'success',
    warning: 'warning',
    negative: 'error',
    primary: 'info',
  };

  return (
    <Flex
      data-test-id={testId}
      role="alert"
      data-intent={intents[variant]}
      aria-live={variant === 'positive' ? 'polite' : 'assertive'}
      className={cx(styles.wrapper, className)}
      {...otherProps}
      ref={ref}
    >
      <Box className={cx(styles.icon)}>{iconVariants[variant]}</Box>
      <Box className={cx(styles.notification)}>
        {title && (
          <Heading
            as="h2"
            className={cx(styles.title)}
            marginBottom="spacingXs"
          >
            {title}
          </Heading>
        )}
        {children && (
          <Paragraph className={styles.content} marginBottom="spacingXs">
            {children}
          </Paragraph>
        )}
        {cta?.label && (
          <TextLink
            {...cta?.textLinkProps}
            as={cta?.textLinkProps?.as || 'button'}
          >
            {cta.label}
          </TextLink>
        )}
      </Box>
      {withCloseButton && (
        <Box>
          <Button
            className={cx(styles.closeButton)}
            variant="transparent"
            startIcon={<CloseIcon />}
