    }
  };

  stopTimer = () => {
    if (this.props.duration === 0) return;

    if (this.timer) {
      clearTimeout(this.timer);
      this.timer = null;
    }
  };

  handleClose = () => {
    this.stopTimer();
    this.setState({ isShown: false });
