import React, { useState, useEffect } from 'react';
import { Paragraph, Stack } from '@contentful/f36-components';
import { MultiSelect } from '@contentful/f36-multiselect';
import { /* useCMA, */ useSDK } from '@contentful/react-apps-toolkit';

// TARGET THE FIELD IN WHICH YOU WANT YOUR APP TO APPEAR
const CONTENT_FIELD_ID = 'segments';


const Field = () => {
  const sdk = useSDK();

  const contentField = sdk.entry.fields[CONTENT_FIELD_ID];

  const spaces = [
    'Travel Blog',
    'Finnace Blog',
    'Fitness App',
    'News Website',
    'eCommerce Catalogue',
    'Photo Gallery'
  ];

  const [ selectedSpaces, setSelectedSpaces ] = useState([]); // Empty array to hold the selected items
  const [ filteredItems, setFilteredItems ] = useState(spaces); 

  const handleSelectItem = (e) => {
    const { checked, value } = e.target;
    if(checked) {
      setSelectedSpaces((prevState) => [...prevState, value]);
    } else {
      const newSelectedSpaces = selectedSpaces.filter(
        (space) => space !== value,
        );
        setSelectedSpaces(newSelectedSpaces)
    }
  };


  return (
    <Stack flexDirection="column" spacing='spacingS'>
      <MultiSelect
        currentSelection={selectedSpaces}
        >
        { spaces.map((space) => {
          const val = space.toLowerCase().replace()
        })}

        </MultiSelect>

    </Stack>
  )
};

export default Field;
