import React, { useState, useEffect } from 'react';
import { Paragraph } from '@contentful/f36-components';
import { /* useCMA, */ useSDK } from '@contentful/react-apps-toolkit';

// TARGET THE FIELD IN WHICH YOU WANT YOUR APP TO APPEAR
const CONTENT_FIELD_ID = 'segments';


const Field = () => {
  const sdk = useSDK();


  const spaces = [
    'Travel Blog',
    'Finnace Blog',
    'Fitness App',
    'News Website',
    'eCommerce Catalogue',
    'Photo Gallery'
  ];


  const [ selectedItems, setSelectedItems ] = useState([]); // Empty array to hold the selected items
  const [ filteredItems, setFilteredItems ] = useState(spaces); 
  return <Paragraph>Hello Entry Field Component (AppId: {sdk.ids.app})</Paragraph>;
};

export default Field;
