import { cx, css } from 'emotion';
import React, { forwardRef } from 'react';
import {
  Box,
  type CommonProps,
  type PropsWithHTMLElement,
  type ExpandProps,
} from '@contentful/f36-core';
import tokens from '@contentful/f36-tokens';

const getStyles = () => {
  return {
    inline: css({
      borderRadius: tokens.borderRadiusMedium,
      boxShadow: `0 0 0 1px ${tokens.gray200}`,
      'th:first-child': {
        borderTopLeftRadius: tokens.borderRadiusMedium,
      },
      'th:last-child': {
        borderTopRightRadius: tokens.borderRadiusMedium,
      },
      'tr:last-child td:first-child': {
        borderBottomLeftRadius: tokens.borderRadiusMedium,
      },
      'tr:last-child td:last-child': {
        borderBottomRightRadius: tokens.borderRadiusMedium,
      },
    }),
    root: css({
      width: '100%',
    }),
  };
};

export type TableInternalProps = CommonProps & {
  layout?: 'inline' | 'embedded';
};

export type TableProps = PropsWithHTMLElement<TableInternalProps, 'table'>;

export const Table = forwardRef<HTMLTableElement, ExpandProps<TableProps>>(
  (
    {
      children,
      className,
      layout = 'inline',
      testId = 'cf-ui-table',
      ...otherProps
    },
    forwardedRef,
  ) => {
    const styles = getStyles();
    return (
      <Box
        cellPadding="0"
        cellSpacing="0"
        {...otherProps}
        as="table"
        display="table"
        ref={forwardedRef}
        className={cx(
          styles.root,
          {
            [styles.inline]: layout === 'inline',
          },
          className,
        )}
        testId={testId}
      >
        {children}
      </Box>
    );
  },
);

Table.displayName = 'Table';
import React, { forwardRef, type ReactElement } from 'react';
import {
  Box,
  type CommonProps,
  type PropsWithHTMLElement,
  type ExpandProps,
} from '@contentful/f36-core';

export type TableBodyInternalProps = CommonProps & {
  children: ReactElement | ReactElement[];
};

export type TableBodyProps = PropsWithHTMLElement<
  TableBodyInternalProps,
  'tbody'
>;

function _TableBody(
  { className, children, testId = 'cf-ui-table-body', ...otherProps },
  forwardedRef,
) {
  return (
    <Box
      {...otherProps}
      as="tbody"
      className={className}
      ref={forwardedRef}
      testId={testId}
    >
      {children}
    </Box>
  );
}

_TableBody.displayName = 'TableBody';

export const TableBody = forwardRef<
  HTMLTableSectionElement,
  ExpandProps<TableBodyProps>
>(_TableBody);
import { css, cx } from 'emotion';
import React, { forwardRef } from 'react';
import tokens from '@contentful/f36-tokens';
import {
  Box,
  type CommonProps,
  type PropsWithHTMLElement,
  type ExpandProps,
} from '@contentful/f36-core';

import { TableCellContext } from './tableCellContext';

export const sortingDirections = {
  asc: 'asc',
  desc: 'desc',
};

export type TableCellSorting = keyof typeof sortingDirections | boolean;

export type TableCellInternalProps = CommonProps & {
  align?: 'center' | 'left' | 'right';
  sorting?: TableCellSorting;
  width?: string | number;
  children?: React.ReactNode;
};

export type TableCellProps = PropsWithHTMLElement<
  TableCellInternalProps,
  'th' | 'td'
>;

export const TableCell = forwardRef<
  HTMLTableCellElement,
  ExpandProps<TableCellProps>
>(
  (
    {
      align = 'left',
      children,
      className,
      sorting = false as TableCellSorting,
      testId = 'cf-ui-table-cell',
      ...otherProps
    },
    forwardedRef,
  ) => {
    return (
      <TableCellContext.Consumer>
        {({ as, name: context, offsetTop }) => {
          const isTableHead = context === 'head';

          return (
            <Box
              {...otherProps}
              as={as}
              className={cx(
                css({
                  backgroundColor: isTableHead ? tokens.gray100 : undefined,
                  borderBottom: `1px solid ${tokens.gray200}`,
                  color: sorting ? tokens.gray900 : tokens.gray700,
                  fontFamily: tokens.fontStackPrimary,
                  fontSize: tokens.fontSizeM,
                  fontWeight: isTableHead
                    ? tokens.fontWeightMedium
                    : tokens.fontWeightNormal,
                  lineHeight: tokens.lineHeightL,
                  padding: `${tokens.spacingS} ${tokens.spacingM}`,
                  textAlign: align,
                  verticalAlign: 'top',
                }),
                className,
              )}
              ref={forwardedRef}
              style={{
                ...otherProps.style,
                top: offsetTop || undefined,
              }}
              testId={testId}
            >
              {children}
            </Box>
          );
        }}
      </TableCellContext.Consumer>
    );
  },
);

TableCell.displayName = 'TableCell';
import { createContext } from 'react';

export type TableCellContextOption = {
  name: 'body' | 'head';
  as: 'td' | 'th';
  offsetTop: number | string;
};

export const contextOptions: {
  body: TableCellContextOption;
  head: TableCellContextOption;
} = {
  body: { name: 'body', as: 'td', offsetTop: 0 },
  head: { name: 'head', as: 'th', offsetTop: 0 },
};

export const TableCellContext = createContext(contextOptions.body);
import { css, cx } from 'emotion';
import React, { forwardRef } from 'react';
import {
  Box,
  type CommonProps,
  type PropsWithHTMLElement,
  type ExpandProps,
} from '@contentful/f36-core';
import tokens from '@contentful/f36-tokens';

import { TableCellContext, contextOptions } from './';

export type TableHeadInternalProps = CommonProps & {
  isSticky?: boolean;
  offsetTop?: number | string;
  children: React.ReactNode;
};

export type TableHeadProps = PropsWithHTMLElement<
  TableHeadInternalProps,
  'thead'
>;

export const TableHead = forwardRef<
  HTMLTableSectionElement,
  ExpandProps<TableHeadProps>
>(
  (
    {
      children,
      className,
      isSticky = false,
      offsetTop,
      testId = 'cf-ui-table-head',
      ...otherProps
    },
    forwardedRef,
  ) => {
    const classNames = cx(
      // For some reason Parcel doesn't build properly if we extract this to
      // a variable ðŸ¤·
      isSticky
        ? css({
            th: {
              position: 'sticky',
              top: 0,
              zIndex: tokens.zIndexDefault,
            },
          })
        : '',
      className,
    );

    return (
      <TableCellContext.Provider
        value={{ ...contextOptions.head, offsetTop: offsetTop || 0 }}
      >
        <Box
          {...otherProps}
          as="thead"
          className={classNames}
          ref={forwardedRef}
          testId={testId}
        >
          {children}
        </Box>
      </TableCellContext.Provider>
    );
  },
);

TableHead.displayName = 'TableHead';
import { css, cx } from 'emotion';
import React, { forwardRef } from 'react';
import tokens from '@contentful/f36-tokens';
import {
  Box,
  type CommonProps,
  type PropsWithHTMLElement,
  type ExpandProps,
} from '@contentful/f36-core';

const getStyles = () => {
  return {
    root: css({
      '&:last-child td': {
        borderBottom: 'none',
      },
      '&:hover td': {
        backgroundColor: tokens.gray100,
      },
    }),
    selected: css({
      'td, th': {
        backgroundColor: tokens.blue100,
      },
      'td:last-child': {
        boxShadow: `inset -2px 0 0 ${tokens.blue500}`,
      },
    }),
  };
};

export type TableRowInternalProps = CommonProps & {
  isSelected?: boolean;
  children: React.ReactNode;
};

export type TableRowProps = PropsWithHTMLElement<TableRowInternalProps, 'tr'>;

export const TableRow = forwardRef<
  HTMLTableRowElement,
  ExpandProps<TableRowProps>
>(
  (
    {
      className,
      children,
      isSelected = false,
      testId = 'cf-ui-table-row',
      ...otherProps
    },
    forwardedRef,
  ) => {
    const styles = getStyles();
    return (
      <Box
        {...otherProps}
        as="tr"
        className={cx(
          styles.root,
          {
            [styles.selected]: isSelected,
          },
          className,
        )}
        ref={forwardedRef}
        testId={testId}
      >
        {children}
      </Box>
    );
  },
);

TableRow.displayName = 'TableRow';
import { Table as OriginalTable } from './Table';
import { TableBody } from './TableBody';
import { TableCell } from './TableCell';
import { TableHead } from './TableHead';
import { TableRow } from './TableRow';

type CompoundTable = typeof OriginalTable & {
  Row: typeof TableRow;
  Head: typeof TableHead;
  Cell: typeof TableCell;
  Body: typeof TableBody;
};

export const Table = OriginalTable as CompoundTable;
Table.Row = TableRow;
Table.Head = TableHead;
Table.Cell = TableCell;
Table.Body = TableBody;
